         [x1,y1]=size(GT_OD);
         
          tp_OD=0;
          tn_OD=0;
          fp_OD=0;
          fn_OD=0;

   for jj=1:x1
       
    
       
        for kk=1:y1
        
            if  GT_OD(jj,kk)==0 && mask3(jj,kk)==1
                 imR(jj,kk)=0;
